import style from './Card.module.css'
import { Link, useLocation } from "react-router-dom";
import {connect} from 'react-redux';
import { addFav, removeFav } from '../../redux/actions';
import { useEffect, useSate, useState} from 'react';

const styleSpecie = {
   display: 'flex',
   flexDirection: 'column', 
   fontSize: '16px',
   color: '#44281d',
   marginRight: '15px',
   marginTop: '-10px'
}

const styleInfo = {
   display: 'flex',
   flexDirection: 'column',
   fontSize: '16px',
   color: ' rgb( 72, 61, 139)',
   marginTop: '-10px'
}

function Card(props) {
   const [ isFav, setIsFav ] = useState(false);
   const { pathname } = useLocation();

   const handleFavorite = () => {
      if(isFav) {
         setIsFav(false)
         deleteFavorite(id)
      }
      else {
         setIsFav(true)
         addFavorite({ id, name, species, gender, image })
      }
   }

   useEffect(() => {
      myFavorites.forEach((fav) => {
         if (fav.id === id) {
            setIsFav(true);
         }
      });
   }, [myFavorites])

   return (
      <div key ={props.id} className={style.divCard}>
         <div className={style.divInfo}>
         {
            isFav ? (
               <button onClick={handleFavorite} className={style.btnFav}>‚ù§Ô∏è</button>
            ) 
            : 
            (
               <button onClick={handleFavorite} className={style.btnFav}>ü§ç</button>
            )
         }
         {
            !pathname.includes('/favorites') && <button className={style.btn} onClick={()=> {props.onClose(props.id)}}>X</button>
         }   
            <Link to={`/detail/${props.id}`}>
               <h2 >{props.id}.- {props.name}</h2>
            </Link>
            <h4 style={styleInfo}>Status: {props.status}</h4>
            <h4 style={styleSpecie}>Specie: {props.species}</h4>
            <h4 style={styleInfo}>Gender: {props.gender}</h4>
         </div>
         <div className={style.divImg}>
            <img  src={props.image} alt={props.name} align='bottom'/>
         </div>
      </div>
   );
}

