import './App.css';
import Cards from './components/Cards/Cards.jsx';
import NavBar from './components/NavBar/NavBar';
// import characters from './data.js';
import { useState } from 'react';
import axios from 'axios';

function App() {
   const [ characters, setCharacters ] = useState([]);
   
   const onSearch = (id) =>{
      axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
         const characterFind = characters.find((char) => char.id === Number(id))
         
         if(characterFind) {
            alert('Already in the list!')
         }

         else if(data.id !== undefined) {
            setCharacters((character) => [...character, data]);
         }
      })

      .catch((error)=> {
         console.log('soy el catch', error);
         alert('Intenta con otro ID')
      })
   };

   const randomHandler = () =>{
      let haveIt = [];
      let random = (Math.random() * 826).toFixed();

      random = Number(random);
      if(!haveIt.includes(random)){
         fetch(`https://rickandmortyapi.com/api/character/${random}`)
         .then((response) => response.json())
         .then((data) =>{
            if(data.name){
               setCharacters((oldCharacter)=>[...oldCharacter,data]);
            }else{
               window.alert('No hay personajes con ese ID');
            }
         });
      }else{
         console.log("Ya agregaste ese ID");
         return false;
      }
   };

   const onClose = (id) =>{
      setCharacters(
         characters.filter((character) => {
           return character.id !== Number(id)
         })
       )
   };
   return (
      <div className='App'>
         <NavBar onSearch = {onSearch} random = {randomHandler}/>
         <Cards
            characters={characters}
            onClose={onClose}
         />
      </div>
   );
}

export default App;
