const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
const regexPasswordNumber =  /^ (?=.*?[0-9]) $/;

const validate = (userData) => {
  let errors = {};

  if(!userData.email){
    errors.email = "Aw Geez! There should be an email.";
  }

  if(!regexEmail.test(userData.email)){
    errors.email = "Aw Geez! Must be an e-mail.";
  }

  if( userData.email.length > 35){
    errors.email = "Aw Geez! You kidding? This email is to big!";
  }

  if( !regexPassword.test(userData.password) ){
    errors.password = "Aw Geez! Must includes a Number!";
  }

  if(userData.password.length < 6  && userData.password.length > 10 ){
    errors.password = "Aw Geez! Must be on 6 to 10 characters";
  }

  return errors;
}

export default validate;
