const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

export const validate = (userData) => {
  let errors = {};



  if(!regexEmail.test(userData.email)){
    errors.email = "Aw Geez! Must be an e-mail";
  }

  return errors;
}
