import SearchBar from "../SearchBar/SearchBar";
import style from './NavBar.module.css'
import { Link, NavLink, useLocation } from "react-router-dom"

export default function NavBar(props){
  const { pathname } = useLocation();
  return(    
    <nav className={style.nav}>
      <NavLink className={style.navLink}>
        <Link to="/about" className={style.navButton}>
          <button className={style.button}>About</button>
        </Link>

        <Link to="/home" className={style.logo}>
          <img src="https://1000marcas.net/wp-content/uploads/2022/04/Rick-and-Morty.png" alt="Logo-R&M" width='100%'/>
        </Link>
        
      </NavLink>
      {
        pathname.includes('/home') && <SearchBar onSearch = {props.onSearch} random = {props.random}/>
      }    
    </nav>
  );  
}
