export const ADD_FAVORITES = 'ADD_FAVORITES';
export const DELETE_FAVORITE = 'DELETE_FAVORITE';

export const addFavorites = (id) =>{
   return{
    type: ADD_FAVORITES,
    payload: id,
  }
}

export const deleteFavorite = (id)=>{
  return{
    type: DELETE_FAVORITE,
    payload: id,
  }
}
